import React from "react";
import { View, Text, Image, StyleSheet } from "react-native";
import tw from "tailwind-react-native-classnames";
import Card from "./Card";
interface Props {
  opponent?: boolean;
}
const Deck: React.FC<Props> = (Props) => {
  const { opponent } = Props;
  return (
    <View style={styles.container}>
      <Card
        width="100%"
        height="100%"
        order={0}
        flipped={!opponent}
        deck
        type="Fire"
      />
      <Card
        width="100%"
        height="100%"
        order={1}
        flipped={!opponent}
        deck
        type="Fire"
      />
      <Card
        width="100%"
        height="100%"
        order={2}
        flipped={!opponent}
        deck
        type="Fire"
      />
      <Card
        width="100%"
        height="100%"
        order={3}
        flipped={!opponent}
        deck
        type="Fire"
      />
      <Card
        width="100%"
        height="100%"
        order={4}
        flipped={!opponent}
        deck
        type="Fire"
        HP={70}
      />
      <Card
        width="100%"
        height="100%"
        order={5}
        flipped={!opponent}
        deck
        type="Fire"
      />
    </View>
  );
};
const styles = StyleSheet.create({
  container: {
    display: "flex",
    flexDirection: "row",
  },
});
export default Deck;
