import React, { useState } from "react";
import {
  StyleSheet,
  ImageBackground,
  Button,
  Alert,
  Image,
  Pressable,
  Vibration,
} from "react-native";

import { Text, View } from "../components/Themed";
import tw from "tailwind-react-native-classnames";
import { TextInput } from "react-native-gesture-handler";
import { RootTabScreenProps } from "../types";
import { pickName1, setSocket } from "../redux/Game";
import { useDispatch, useSelector } from "react-redux";
import { io } from "socket.io-client";
import { RootState } from "../redux/store";
export default function TabTwoScreen({
  navigation,
}: RootTabScreenProps<"TabTwo">) {
  const [ text, onChangeText ] = useState("");
  const { token } = useSelector((state: RootState) => state.user);
  const dispatch = useDispatch();

  const connect = () => {
    if (text.length > 0) {
      dispatch(pickName1(text));
    }
    navigation.navigate("Choose");
    Vibration.vibrate();

    dispatch(
      setSocket(
        io("https://mighty-inlet-28867.herokuapp.com", {
          query: {
            token: token,
          },
        })
      )
    );
  };
  return (
    <View style={styles.container}>
      <Text style={[ tw`text-2xl text-white  mt-20`, styles.compete ]}>
        Compete To Win New Cards
      </Text>
      <View
        style={[
          tw` absolute bottom-0 w-full flex flex-col  items-center`,
          styles.bottomCont,
        ]}
      >
        <Image
          style={styles.image}
          source={require("../assets/images/pikachu.png")}
        />
        <View
          style={[
            tw` absolute bottom-0 bg-white  flex flex-col justify-center items-center w-full p-20`,
            styles.bottom,
          ]}
        >
          <TextInput
            style={styles.input}
            onChangeText={onChangeText}
            value={text}
            placeholder="Player name"
          />

          <Pressable
            onPress={() => {
              connect();
            }}
            style={({ pressed }) => [
              {
                backgroundColor: pressed ? "rgb(210, 230, 255)" : "#F4FD19",
              },
              styles.wrapperCustom,
            ]}
          >
            <Text style={styles.button}>Play Now</Text>
          </Pressable>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    backgroundColor: " rgb(255,62,62))",
  },

  title: {
    fontSize: 20,
    fontWeight: "bold",
  },
  compete: {
    fontSize: 28,
    fontFamily: "space-mono",
    // color: "#fcd116",
    fontWeight: "bold",
  },

  image: {
    width: 130,
    height: 130,
  },
  wrapperCustom: {
    borderRadius: 8,

    textAlign: "center",
  },
  button: {
    backgroundColor: "#fff01b",
    padding: 10,
    borderRadius: 15,
  },
  input: {
    height: 40,
    width: 250,
    margin: 12,
    borderWidth: 1,
    padding: 10,
    borderRadius: 15,
  },
  bottomCont: {
    backgroundColor: " rgb(255,62,62))",
    height: "46%",
  },
  bottom: {
    borderTopRightRadius: 40,
    borderTopLeftRadius: 40,
  },
});
