import React from "react";
import {
  View,
  Text,
  Image,
  StyleSheet,
  TouchableOpacity,
  ImageBackground,
} from "react-native";
import tw from "tailwind-react-native-classnames";

//TODO change ID to gameId
interface Props {
  width: string;
  order?:number;
  height: string;
  id: number;
  deck?: boolean;
  flipped?: boolean;
  //data
  cardName: string;
  type: string;
  HP: number;
}
const Card: React.FC<Props> = (Props) => {
  const { width, height, deck, id, order,flipped, type, HP, cardName } = Props;

  return (
    <View>
      {flipped ? (
        <ImageBackground
          style={styles.cardFace}
          //todo change fire into type when you fix the issues
          source={require(`../assets/images/${`Fire`}.png`)}
        >
          <Text>{HP}</Text>
          <Text>{type}</Text>
          <Text>{cardName}</Text>
        </ImageBackground>
      ) : (
        <Image
          style={[
            { width, height },
            { transform: [ { translateX: deck ? id * -50 : 0 } ] },
          ]}
          source={require("../assets/images/cardback.png")}
        />
      )}
    </View>
  );
};
const styles = StyleSheet.create({
  cardFace: {
    width: "100%",
    height: "100%",
    position: "relative",
  },
});

export default Card;
