const getPartner = (room , socket)=>{

  let partner ; 

  if (room[socket.id]) {
    partner = room[socket.id].partener;
    return partner;
  } else {
    for (let i in room) {
      if (room[i].partener.id === socket.id) {
        partner = room[i];
        return partner;
      }
    }
  }

}
export default getPartner;
